import React, { Component } from "react";

class Navbar extends Component {
  state = {
    boton: "circulo",
    forma: "nav-bar d-none fixed-top"
  };
  handlerNavIn = e => {
    e.preventDefault();
    const barra = "nav-bar ";
    const boton = "circulo-econdido"
    this.setState({ 
      boton: boton,
      forma: barra
    });
  };
  handlerNavOut = e => {
    e.preventDefault();
    const navE = "nav-bar-escondida";
    const navL = "nav-bar";
    const circuloE = "circulo-econdido"
    const circuloV = "circulo"
    this.state.forma === navL
      ? this.setState({ forma: navE, boton: circuloV })
      : this.setState({ forma: navL, boton: circuloE });
  };
  render() {
    return (
      <div className="contenido fixed-top">
        <div className={this.state.boton} onMouseOver={this.handlerNavIn}></div>
        <div className={this.state.forma} onMouseOut={this.handlerNavOut}>
          <h5>Who i am</h5>
          <h5>Skills</h5>
          <h5>Studies</h5>
          <h5>Contact me</h5>
        </div>
      </div>
    );
  }
}

export default Navbar;
