import React, { Component } from "react";

class Navbar extends Component {
  state = {
    boton: "circulo",
    forma: "nav-bar d-none fixed-top"
  };
  handlerNavIn = e => {
    e.preventDefault();
    const barra = "nav-bar fixed-top";
    const boton = "circulo d-none"
    this.setState({ 
      boton: boton,
      forma: barra
    });
  };
  handlerNavOut = e => {
    e.preventDefault();
    const largo = "nav-bar fixed-top ";
    const corto = "nav-bar d-none fixed-top";
    this.state.forma === largo
      ? this.setState({ forma: corto })
      : this.setState({ forma: largo });
  };
  render() {
    return (
      <div className="contenido fixed-top">
        <div
          className="circulo "
          onMouseOver={this.handlerNavIn}
        >GO
        </div>
        <div className={this.state.forma} onMouseOut={this.handlerNavOut}>
          <h5>Who i am</h5>
          <h5>Skills</h5>
          <h5>Studies</h5>
          <h5>Contact me</h5>
        </div>
      </div>
    );
  }
}

export default Navbar;
