import React, { Component } from "react";

class Navbar extends Component {
  state = {
    boton: "circulo d-block",
    forma: "nav-bar d-none fixed-top"
  };
  handlerNavIn = e => {
    e.preventDefault();
    const largo = "nav-bar fixed-top";
    this.setState({ forma: largo });
  };
  handlerNavOut = e => {
    e.preventDefault();
    const largo = "nav-bar fixed-top ";
    const corto = "nav-bar d-none fixed-top";
    this.state.forma === largo
      ? this.setState({ forma: corto })
      : this.setState({ forma: largo });
  };
  render() {
    return (
      <>
        <div
          className="circulo"
          onMouseOver={this.handlerNavIn}
        >GO
        </div>
        <div className={this.state.forma} onMouseOut={this.handlerNavOut}>
          <h5>Who i am</h5>
          <h5>Skills</h5>
          <h5>Studies</h5>
          <h5>Contact me</h5>
        </div>
      </>
    );
  }
}

export default Navbar;
