import React, { Component } from "react";

class Navbar extends Component {
  state = {
    boton: "boton",
    forma: "nav-bar"
  };
  handlerNavIn = e => {
    e.preventDefault();
    const navL = "nav-bar ";
    const botonE = "boton-econdido"
    this.setState({ 
      boton: botonE,
      forma: navL
    });
  };
  handlerNavOut = e => {
    e.preventDefault();
    const navE = "nav-bar-escondida ";
    const navL = "nav-bar ";
    const botonE = "boton-econdido"
    const botonV = "boton"
    this.state.forma === navL
      ? this.setState({ forma: navE, boton: botonV })
      : this.setState({ forma: navL, boton: botonE });
  };
  render() {
    return (
      <div className="Barra-nav fixed-top">
        <div className={this.state.boton} onClick={this.handlerNavIn}></div>
        <div className={this.state.forma} onMouseLeave={this.handlerNavOut}>
          <h5>Who i am</h5>
          <h5>Skills</h5>
          <h5>Studies</h5>
          <h5>Contact me</h5>
        </div>
      </div>
    );
  }
}

export default Navbar;
